$(document).ready(function() {
	$('#event&promo').click(function() {
        $('html, body').animate({
            scrollTop: '2000px'
        }, 1000);
        return false;
    });
    
    $('#contact').click(function() {
        $('html, body').animate({
            scrollTop: '3000px'
        }, 1500);
        return false;
    });
	

});// JavaScript Document